```tsx
import { execution } from "@dynatrace-sdk/automation-utils";
import { businessEventsClient } from "@dynatrace-sdk/client-classic-environment-v2";

export default async function ({ execution_id }) {
  const exe = await execution(execution_id);

  const result = await exe.result("fetch_currency_rates");
  const { quotes, source, timestamp } = result;

  for (const key in quotes) {
    if (Object.prototype.hasOwnProperty.call(quotes, key)) {
      const currency = key.substring(3); 

      const bizevent = {
        specversion: "1.0",
        source: "CurrencyLayer",
        id: crypto.randomUUID().toString(),
        type: "currency.rates",
        data: {
          rate: quotes[key],
          currency,
          timestamp,
        },
      };


      businessEventsClient
        .ingest({
          body: bizevent,
          type: "application/json; charset=utf-8",
        })
        .then(() => console.log("Event ingested"))
        .catch((e) => console.error("Failed to ingest event: " + e));
    }
  }
}

```
